import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';
import { Input, Button, Select } from 'antd';

const ADD_CAR = gql`
  mutation AddCar($year: Int!, $make: String!, $model: String!, $price: Float!, $personId: ID!) {
    addCar(year: $year, make: $make, model: $model, price: $price, personId: $personId) {
      id
      year
      make
      model
      price
      personId
    }
  }
`;

const CarForm = ({ people }) => {
  const [year, setYear] = useState('');
  const [make, setMake] = useState('');
  const [model, setModel] = useState('');
  const [price, setPrice] = useState('');
  const [personId, setPersonId] = useState('');
  const [addCar] = useMutation(ADD_CAR, {
    update(cache, { data: { addCar } }) {
      cache.modify({
        fields: {
          cars(existingCars = []) {
            const newCarRef = cache.writeFragment({
              data: addCar,
              fragment: gql`
                fragment NewCar on Car {
                  id
                  year
                  make
                  model
                  price
                  personId
                }
              `
            });
            return [...existingCars, newCarRef];
          }
        }
      });
    }
  });

  const handleSubmit = () => {
    addCar({ variables: { year: parseInt(year), make, model, price: parseFloat(price), personId } });
    setYear('');
    setMake('');
    setModel('');
    setPrice('');
    setPersonId('');
  };

  return (
    <div>
      <Input
        value={year}
        onChange={e => setYear(e.target.value)}
        placeholder="Year"
      />
      <Input
        value={make}
        onChange={e => setMake(e.target.value)}
        placeholder="Make"
      />
      <Input
        value={model}
        onChange={e => setModel(e.target.value)}
        placeholder="Model"
      />
      <Input
        value={price}
        onChange={e => setPrice(e.target.value)}
        placeholder="Price"
      />
      <Select
        value={personId}
        onChange={value => setPersonId(value)}
        placeholder="Select Person"
        style={{ width: 200 }}
      >
        {people.map(person => (
          <Select.Option key={person.id} value={person.id}>
            {person.firstName} {person.lastName}
          </Select.Option>
        ))}
      </Select>
      <Button onClick={handleSubmit}>Add Car</Button>
    </div>
  );
};

export default CarForm;
